export const actionPending     = () => ({ type: 'DATA_USER', status: 'PENDING', payload: null, error: null });
export const actionResolved    = payload => ({ type: 'DATA_USER', status: 'RESOLVED', payload, error: null });
export const actionRejected    = error => ({ type: 'DATA_USER', status: 'REJECTED', payload: null, error });
export const API = 'http://api.tvmaze.com/search/shows?q=';
export const KeyNames = new Set(['name', 'language', 'genres', 'status','rating']);
export const DATA_USER = 'DATA_USER';
export const ADD_USER = 'ADD_USER';


